import { useEffect, useRef, useState } from 'react';
import './App.css';
import TodoForm from './components/TodoForm';
import firebaseApp from './Firebase';
import {getDatabase, onValue, push, ref, update} from 'firebase/database'

function App() {

  const db = getDatabase(firebaseApp)
  var [btnTxt,setBtnTxt]=useState('Add New Task')
  var [title,setTitle]=useState('')
  var [todolist,setTodoList]=useState([])
  var [editTitle,setEditTitle]=useState('')
  var inpRef = useRef()
  const todoRef = ref(db,"/todos");


  useEffect(()=>{
    onValue(todoRef, (snapshot)=>{
      const todos = snapshot.val()
      const newTodoList=[]

      for(let id in todos){
        newTodoList.push({id,...todos[id]});
      }
      setTodoList(newTodoList)
    });
  },[db])

  const inpChangeHandler=(e)=>{
    setTitle(e.target.value)
  }

  const addData=()=>{
    if(btnTxt=='Add New Task'){
    const todo={
      title,
      done:false
    }
    push(todoRef,todo)
    inpRef.current.value=''
  }
  else{
    update(todoRef,{done:!editTitle.done})
    set
  }
  }

  const editHandler=(todo)=>{
    inpRef.current.value=todo.title
    const todoRef = ref(db, "/todos/" + todo.id);
    setEditTitle(todoRef)
    setBtnTxt('Update Text')
    // 
    // console.log(todoRef)
  }

  return (
    <div className="container">
      <h1>TodoInput</h1>
      <TodoForm btnTxt={btnTxt} addData={addData} inpChangeHandler={inpChangeHandler} inpRef={inpRef} todolist={todolist} editHandler={editHandler}/>
    </div>
  );
}

export default App;
