import { createSlice } from "@reduxjs/toolkit";
import firebaseApp from '../Firebase';
import {getDatabase, onValue, push, ref, remove, update} from 'firebase/database'

const db = getDatabase(firebaseApp)
var todoRef = ref(db,"/todos");

var initialState ={

}

const todoSlice =createSlice({
    name:'todo',
    initialState,
    reducers:{
        addText:(state,action)=>{
            if(action.payload.btnTxt=='Add New Task'){
                push(todoRef,action.payload.todo)
            }
            else{
                update(todoRef,{title:action.payload})
            }
        },
        editText:(state,action)=>{
            todoRef = ref(db, "/todos/" + action.payload);
        },
        deleteData:(state,action)
    }
})

export const {addText,editText} = todoSlice.actions

export default todoSlice.reducer
