import { useEffect, useRef, useState } from 'react';
import './App.css';
import TodoForm from './components/TodoForm';
import firebaseApp from './Firebase';
import {getDatabase, onValue, push, ref, remove, update} from 'firebase/database'

function App() {

  const db = getDatabase(firebaseApp)
  var [btnTxt,setBtnTxt]=useState('Add New Task')
  var [title,setTitle]=useState('')
  var [todolist,setTodoList]=useState([])
  var [edit,setEdit]=useState('')
  var inpRef = useRef()
  const todoRef = ref(db,"/todos");
  useEffect(()=>{
    onValue(todoRef, (snapshot)=>{
      const todos = snapshot.val()
      const newTodoList=[]
      for(let id in todos){
        newTodoList.push({id,...todos[id]});
      }
      setTodoList(newTodoList)
    });
  },[db])

// Function shows all data
  const showAllData=()=>{
    setTodoList(todolist)
  }
// Function shows done data 
  const showCompData=()=>{
    var completed=[]
    let temp=
    todolist.map((item)=>{
      if(item.done){
        completed.push(item)
      }
    })
    setTodoList(completed)
  }
// Function shows incompleted Data
  const showInCompData=()=>{
    var incompleted=[]
    todolist.map((item)=>{
      if(!item.done){
        incompleted.push(item)
      }
    })
    setTodoList(incompleted)
  } 
  const inpChangeHandler=(e)=>{
    setTitle(e.target.value)
  }
// function adds data in a list
  const addData=()=>{
    if(btnTxt=='Add New Task'){
      const todo={
        title,
        done:false
      }
      push(todoRef,todo)
    }
    else{
      console.log(edit)
      update(edit,{title:inpRef.current.value})
      setBtnTxt('Add New Task')
    }
    inpRef.current.value=''
  }
// Function Edits Data
  const editHandler=(id,title)=>{
    inpRef.current.value=title
    const todoRef = ref(db, "/todos/" + id);
    console.log(todoRef)
    setEdit(todoRef)
    setBtnTxt('Update Text')
  }
// function check Uncheck the completed and incompleted tasks 
  const checkHandler=(id,item)=>{
    if(!item.done){
      const todoRef = ref(db,"/todos/"+id)
      update(todoRef,{done:true})
    }
    else{
      const todoRef = ref(db,"/todos/"+id)
      update(todoRef,{done:false})
    }
  }
// Function deletes Task
  const deleteHandler=(id)=>{
    const del = ref(db,"/todos/"+id)
    remove(del)
  }
// Function Deletes All Tasks
  const deleteAll =()=>{
    remove(ref(db,"/todos"))
  }
// Function deletes Completed tasks
  const deleteCompleted =()=>{
    todolist.map((item)=>{
      if(item.done){
        remove(ref(db,"/todos/"+item.id))
      }
    })
  }

  return (
    <div className="container">
      <h1>TodoInput</h1>
      <TodoForm btnTxt={btnTxt} addData={addData} inpChangeHandler={inpChangeHandler} inpRef={inpRef} todolist={todolist} editHandler={editHandler} deleteAll={deleteAll} deleteHandler={deleteHandler} checkHandler={checkHandler} deleteCompleted={deleteCompleted} showAllData={showAllData} showCompData={showCompData} showInCompData={showInCompData}/>
    </div>
  );
}

export default App;
