import { createSlice } from "@reduxjs/toolkit";
import firebaseApp from '../Firebase';
import {getDatabase, onValue, push, ref, remove, update} from 'firebase/database'

const db = getDatabase(firebaseApp)
const todoRef = ref(db,"/todos");

var initialState ={
    btnTxt:'Add new Task'
}

const todoSlice =createSlice({
    name:'todo',
    initialState,
    reducers:{
        addText:(state,action)=>{
            if(state.btnTxt=='Add new Task'){
                push(todoRef,action.payload)
            }
            else{
                update(action.payload.ed,{title:inpRef.current.value})
            }
        }
    }
})

export const {addText} = todoSlice.actions

export default todoSlice.reducer
