import { useEffect, useRef, useState } from 'react';
import './App.css';
import TodoForm from './components/TodoForm';
import firebaseApp from './Firebase';
import {getDatabase, onValue, push, ref, remove, update} from 'firebase/database'

function App() {

  const db = getDatabase(firebaseApp)
  var [btnTxt,setBtnTxt]=useState('Add New Task')
  var [title,setTitle]=useState('')
  var [todolist,setTodoList]=useState([])
  var [edit,setEdit]=useState('')
  var [cutSty,setCutSty]=useState('none__line')
  var inpRef = useRef()
  const todoRef = ref(db,"/todos");


  useEffect(()=>{
    onValue(todoRef, (snapshot)=>{
      const todos = snapshot.val()
      const newTodoList=[]

      for(let id in todos){
        newTodoList.push({id,...todos[id]});
      }
      setTodoList(newTodoList)
    });
  },[db])

  const inpChangeHandler=(e)=>{
    setTitle(e.target.value)
  }

  const addData=()=>{
    if(btnTxt=='Add New Task'){
      const todo={
        title,
        done:false
      }
      push(todoRef,todo)
    }
    else{
      console.log(edit)
      update(edit,{title:inpRef.current.value})
      setBtnTxt('Add New Task')
    }
    inpRef.current.value=''
  }

// Function Edits Data

  const editHandler=(id,title)=>{
    inpRef.current.value=title
    const todoRef = ref(db, "/todos/" + id);
    console.log(todoRef)
    setEdit(todoRef)
    setBtnTxt('Update Text')
  }

  const checkHandler=(id,item)=>{
    if(!item.done){
      const todoRef = ref(db,"/todos/"+id)
      update(todoRef,{done:true})
      setCutSty()
    }
    else{
      const todoRef = ref(db,"/todos/"+id)
      update(todoRef,{done:false})
      setCutSty('none__line')
    }
  }


// Function deletes Task
  const deleteHandler=(id)=>{
    const del = ref(db,"/todos/"+id)
    remove(del)
  }
// Function Deletes All Tasks
  const deleteAll =()=>{
    remove(ref(db,"/todos"))
  }
  return (
    <div className="container">
      <h1>TodoInput</h1>
      <TodoForm btnTxt={btnTxt} addData={addData} inpChangeHandler={inpChangeHandler} inpRef={inpRef} todolist={todolist} editHandler={editHandler} deleteAll={deleteAll} deleteHandler={deleteHandler} checkHandler={checkHandler}/>
    </div>
  );
}

export default App;
